<script setup>
import { ref, computed, onMounted } from "vue";
import { useCiudadStore } from "../stores/ciudad.js";
const useCiudad = useCiudadStore();

let ciudadEncontrada = ref([]);
let buscado_v = ref("");
let vacio = ref("");
let msg_modal = ref("");
let codigo = ref("");
let nombre = ref("");
let daneciudad = ref("");
let region = ref("");
let departamento = ref("");
let danedepartamento = ref("");
let id = ref("");
let errores = ref("");
let alert = ref(false);

const setDocumentTitle = () => {
  document.title = `COLMABE || Ciudades`;
};

function cambio(msg) {
  if (msg == "Agregar") {
    msg_modal.value = "Agregar Ciudad";
    limpiar();
  } else {
    msg_modal.value = "Editar Ciudad";
  }
}

async function buscarCiudad() {
  await useCiudad.buscarCiudad().then((res) => {
    ciudadEncontrada.value = res;
  });
}

const ciudad_filtrado = computed(() => {
  if (buscado_v.value === "") {
    buscarCiudad();
    vacio.value = "";
  } else {
    let filtrado = ciudadEncontrada.value.filter((g) =>
      g.nombre.includes(buscado_v.value.toLowerCase())
    );
    if (filtrado.length === 0) {
      vacio.value = `Sin resultados para ${buscado_v.value}`;
      ciudadEncontrada.value = [];
    } else {
      vacio.value = "";
      ciudadEncontrada.value = filtrado;
    }
  }
});

async function registrarCiudad() {
  await useCiudad
    .registrarCiudad({
      nombre: nombre.value.toLowerCase(),
      daneciudad: daneciudad.value.toLowerCase(),
      region: region.value.toLowerCase(),
      departamento: departamento.value.toLowerCase(),
      danedepartamento: danedepartamento.value.toLowerCase(),
      estado: true,
    })
    .then(() => {
      buscarCiudad();
      ok();
      closeModal();
      limpiar();
    })
    .catch((err) => {
      if (err.response && err.response.data.errors) {
        alert.value = true;
        errores.value = err.response.data.errors[0].msg;
        alerta();
      } else if (err.response && err.response.data.msg) {
        alert.value = true;
        errores.value = err.response.data.msg;
        alerta();
      }
    });
}

async function editar(c) {
  id.value = c._id;
  (nombre.value = c.nombre),
    (daneciudad.value = c.daneciudad),
    (region.value = c.region),
    (departamento.value = c.departamento),
    (danedepartamento.value = c.danedepartamento);
}

async function guardarEdision() {
  await useCiudad
    .editarCiudad(
      id.value,
      nombre.value.toLowerCase(),
      daneciudad.value.toLowerCase(),
      region.value.toLowerCase(),
      departamento.value.toLowerCase(),
      danedepartamento.value.toLowerCase()
    )
    .then(() => {
      buscarCiudad();
      ok();
      closeModal();
      limpiar();
    })
    .catch((err) => {
      if (err.response && err.response.data.errors) {
        alert.value = true;
        errores.value = err.response.data.errors[0].msg;
        alerta();
      } else if (err.response && err.response.data.msg) {
        alert.value = true;
        errores.value = err.response.data.msg;
        alerta();
      }
    });
}

async function cambiarEstado(c) {
  if (c.estado === true) {
    c.estado = false;
  } else {
    c.estado = true;
  }
  await useCiudad.cambiarEstado(c._id, c.estado).then(() => {
    console.log("Cambio de estado realizado correctamente");
  });
}

function limpiar() {
  (nombre.value = ""),
    (daneciudad.value = ""),
    (region.value = ""),
    (departamento.value = ""),
    (danedepartamento.value = "");
}

function closeModal() {
  document.getElementById("close").click();
}

function ok() {
  Swal.fire({
    position: "center",
    icon: "success",
    title: "Cambios guardados",
    showConfirmButton: false,
    timer: 1500,
  });
}

function alerta() {
  setTimeout(() => {
    alert.value = false;
    errores.value = "";
  }, 1600);
}

onMounted(async () => {
  setDocumentTitle();
  await buscarCiudad();
  await traigodatos();
});

import { useConfiguracionStore } from "../stores/configuracion.js";
const useConfiguracion = useConfiguracionStore();
let coloroficialmenu = ref();
let coloroficialletra = ref();
let coloroficialtres = ref();
let coloroficialcuatro = ref();
async function traigodatos() {
  await useConfiguracion
    .traerConfiguracion()
    .then((resultado) => {
      coloroficialmenu.value = resultado[0].colormenu;
      coloroficialletra.value = resultado[0].colorletra;
      coloroficialtres.value = resultado[0].colortres;
      coloroficialcuatro.value = resultado[0].colorcuatro;
    })
    .catch((error) => {
      console.error("Hubo un error:", error);
    });
}
</script>

