<script setup>
import { ref, onMounted } from "vue";
import { useRouter } from "vue-router";
import { useUsuarioStore } from "../stores/usuario";
import VueJwtDecode from "vue-jwt-decode";
const useUsuario = useUsuarioStore();

let token = ref(null);

import { useConfiguracionStore } from "../stores/configuracion.js";
const useConfiguracion = useConfiguracionStore();
let coloroficialmenu = ref();
let colortres = ref();
let colorcuatro = ref();
let photo = ref('');
let nombreUsuario = ref('');

async function traigodatos() {
  await useConfiguracion
    .traerConfiguracion()
    .then((resultado) => {
      coloroficialmenu.value = resultado[0].colormenu;
      colortres.value = resultado[0].colortres;
      colorcuatro.value = resultado[0].colorcuatro;
    })
    .catch((error) => {
      console.error("Hubo un error:", error);
    });
}

async function decoded() {
  token.value = VueJwtDecode.decode(useUsuario.token);
  await useUsuario.buscarUsuarioId(token.value.id).then(()=>{
    console.log(useUsuario.usuarioId.archivo);
    foto();
    nombreUsuario.value=useUsuario.usuarioId.nombre
  }).then((err)=>{
    console.log(err);
  })
  
};

async function foto(){
await useUsuario
        .mostrarArchivoCloud(useUsuario.usuarioId._id)
        .then((res) => {
          photo.value = res.data.url;
        })
        .catch((err) => {
          console.log(err);
        });
      };

decoded();

const router = useRouter();

function limpiarLocalStorage() {
  localStorage.clear();
}

function cerrarsesion() {
  Swal.fire({
    title: "¿Seguro quieres cerrar sesion?",
    showDenyButton: true,
    confirmButtonText: "Confirmar",
    confirmButtonColor: coloroficialmenu.value,
    denyButtonText: `Cancelar`,
  }).then((result) => {
    limpiarLocalStorage();
    if (result.isConfirmed) {
      Swal.fire({
        title: "¡Gracias por utilizar sena software!",
        confirmButtonColor: coloroficialmenu.value,
      });

      (useUsuario.token = null), router.push("/");
    }
  });
}

onMounted(traigodatos);
</script>

