<script setup>
import { ref, computed, onMounted } from "vue";
import { useProgramaStore } from "../stores/programa.js";
import { useUsuarioStore } from "../stores/usuario.js";

const useUsuario = useUsuarioStore();
const usePrograma = useProgramaStore();

let usuarios = ref([]);
let vacio = ref("");
let buscado_v = ref("");
let dato_perfil = ref([]);
let instructores = ref([]);
let instructorId = ref("");
let alert = ref(false);
let errores = ref("");
let newIntructores = ref([]);

const setDocumentTitle = () => {
  document.title = `Legado Sena || Instructores ${usePrograma.idPrograma.nombre}`;
};

async function instructoresPrograma() {
  await usePrograma.buscarProgramaId(usePrograma.idPrograma._id);
  usuarios.value = usePrograma.idPrograma.usuario;
  newIntructores.value = [...usuarios.value]
}

async function agregarInstructorPrograma() {
  await usePrograma
    .agregarInstructor(usePrograma.idPrograma._id, instructorId.value)
    .then(() => {
      instructoresPrograma();
      ok();
      document.getElementById("close1").click();
    })
    .catch((err) => {
      if (err.response && err.response.data.errors) {
        alert.value = true;
        errores.value = err.response.data.errors[0].msg;
        alerta();
      } else if (err.response && err.response.data.msg) {
        alert.value = true;
        errores.value = err.response.data.msg;
        alerta();
      } else if (err.response.data.error) {
        alert.value = true;
        errores.value = "Falta el archivo";
        alerta();
      }
    });
}

instructoresPrograma();

async function buscarUsuario() {
  await useUsuario.buscarUsuario().then((res) => {
    instructores.value = res.filter(
      (i) =>
        i.rol.denominacion === "Instructor".toLowerCase() &&
        i.red._id === useUsuario.usuarioId.red._id
    );
  });
};

function usuario_filtrado (event) {
  buscado_v.value = event.target.value;
  if (buscado_v.value.trim() === "") {
    vacio.value = "";
    instructoresPrograma();
  } else {
    usuarios.value = newIntructores.value;
    let filtrado = usuarios.value.filter(
      (g) =>
        g.nombre.includes(buscado_v.value.trim().toLowerCase()) ||
        g.identificacion.includes(buscado_v.value.trim().toLowerCase())
    );
    usuarios.value = [];
    if (filtrado.length === 0) {
      vacio.value = `Sin resultados para ${buscado_v.value}`;
    } else {
      vacio.value = "";
      usuarios.value = filtrado;
    }
  }
};

async function cambiarEstado(u) {
  if (u.estado === true) {
    u.estado = false;
  } else {
    u.estado = true;
  }
  await useUsuario
    .cambiarEstado(u._id, u.estado)
    .then(() => {
      console.log("Cambio exitoso");
    })
    .catch((err) => {
      console.log(err);
    });
}

function perfil(u) {
  dato_perfil.value = [];
  dato_perfil.value = u;
}

function ok() {
  Swal.fire({
    position: "center",
    icon: "success",
    title: "Cambios guardados",
    showConfirmButton: false,
    timer: 1500,
  });
}

function closeModal() {
  document.getElementById("close").click();
}

function alerta() {
  setTimeout(() => {
    alert.value = false;
    errores.value = "";
  }, 3000);
}

onMounted(async () => {
  setDocumentTitle(), await traigodatos();
});

import { useConfiguracionStore } from "../stores/configuracion.js";
const useConfiguracion = useConfiguracionStore();
let coloroficialmenu = ref();
let coloroficialletra = ref();
let coloroficialtres = ref();
let coloroficialcuatro = ref();
async function traigodatos() {
  await useConfiguracion
    .traerConfiguracion()
    .then((resultado) => {
      coloroficialmenu.value = resultado[0].colormenu;
      coloroficialletra.value = resultado[0].colorletra;
      coloroficialtres.value = resultado[0].colortres;
      coloroficialcuatro.value = resultado[0].colorcuatro;
    })
    .catch((error) => {
      console.error("Hubo un error:", error);
    });
}
</script>

