<script setup>
import { ref, computed, onMounted } from "vue";
import { useMaterialFormacionStore } from "../stores/materialFormacion.js";

const useMaterialFormacion = useMaterialFormacionStore();

let msg_modal = ref("");
let buscado_v = ref("");
let vacio = ref("");
let errores = ref("");
let alert = ref(false);
let materialesFormacion = ref([]);
let id = ref("");
let nombre = ref("");
let tipo = ref("");
let descripcion = ref("");
let archivo = ref(null);
let pageTitle = ref("");
let newMateriales = ref([]);

const setDocumentTitle = () => {
  pageTitle.value = "Materiales de Formacion";
  document.title = `Legado Sena || ${pageTitle.value}`;
};

function cambio(msg) {
  if (msg == "Agregar") {
    limpiar();
    msg_modal.value = "Agregar Material de Formación";
    limpiar();
  } else {
    msg_modal.value = "Editar Material de Formación";
  }
}

async function buscarMaterialesFormacion() {
  await useMaterialFormacion.buscarMaterialFormacion().then((res) => {
    materialesFormacion.value = res;
    newMateriales.value = [...materialesFormacion.value];
  });
}

function programa_filtrado(event) {
  buscado_v.value = event.target.value;
  if (buscado_v.value === "") {
    buscarMaterialesFormacion();
    vacio.value = "";
  } else {
    materialesFormacion.value = newMateriales.value;
    let filtrado = materialesFormacion.value.filter((g) =>
      g.nombre.includes(buscado_v.value.toLowerCase())
    );
    materialesFormacion.value = [];
    if (filtrado.length === 0) {
      vacio.value = `Sin resultados para ${buscado_v.value}`;
    } else {
      vacio.value = "";
      materialesFormacion.value = filtrado;
    }
  }
}

function llevar(event) {
  archivo.value = event.target.files[0];
}

function validaciones() {
  if (nombre.value.trim() === "") {
    alert.value = true;
    errores.value = "Complete el campo Nombre";
    alerta();
  } else if (tipo.value.trim() === "") {
    alert.value = true;
    errores.value = "Complete el campo Tipo";
    alerta();
  } else if (descripcion.value.trim() === "") {
    alert.value = true;
    errores.value = "Complete el campo Descripción";
    alerta();
  } else if (archivo.value.trim() === "") {
    alert.value = true;
    errores.value = "Falta el Archivo";
    alerta();
  } else {
    return true;
  }
}

async function registarMaterialFormacion() {
  if (validaciones() === true) {
    await useMaterialFormacion
      .registrarMaterialFormacion({
        nombre: nombre.value.toLowerCase(),
        tipo: tipo.value.toLowerCase(),
        descripcion: descripcion.value.toLowerCase(),
        archivo: archivo.value,
        estado: true,
      })
      .then(() => {
        ok();
        closeModal();
        buscarMaterialesFormacion();
      })
      .catch((err) => {
        console.log(err);
        if (err.response && err.response.data.errors) {
          alert.value = true;
          errores.value = err.response.data.errors[0].msg;
          alerta();
        } else if (err.response && err.response.data.msg) {
          alert.value = true;
          errores.value = err.response.data.msg;
          alerta();
        } else if (err.response.data.error) {
          alert.value = true;
          console.log(err);
          errores.value = "Falta el archivo";
          alerta();
        }
      });
  }
}

function editar(p) {
  console.log(p);
  (id.value = p._id),
    (nombre.value = p.nombre),
    (tipo.value = p.tipo),
    (descripcion.value = p.descripcion),
    (archivo.value = p.archivo);
}

async function guardarEdision() {
  if (validaciones() === true) {
    await useMaterialFormacion
      .editarMaterialFormacion(
        id.value,
        nombre.value.toLowerCase(),
        tipo.value.toLowerCase(),
        descripcion.value.toLowerCase(),
        archivo.value
      )
      .then(() => {
        ok();
        closeModal();
        buscarMaterialesFormacion();
      })
      .catch((err) => {
        if (err.response && err.response.data.errors) {
          alert.value = true;
          errores.value = err.response.data.errors[0].msg;
          alerta();
        } else if (err.response && err.response.data.msg) {
          alert.value = true;
          errores.value = err.response.data.msg;
          alerta();
        } else if (err.response.data.error) {
          alert.value = true;
          console.log(err);
          errores.value = "Falta el archivo";
          alerta();
        }
      });
  }
}

async function cambiarEstado(p) {
  if (p.estado === true) {
    p.estado = false;
  } else {
    p.estado = true;
  }
  await useMaterialFormacion
    .cambiarEstado(p._id, p.estado)
    .then(() => {
      console.log("Cambio exitoso");
    })
    .catch((err) => {
      console.log(err);
    });
}

function ok() {
  Swal.fire({
    position: "center",
    icon: "success",
    title: "Cambios guardados",
    showConfirmButton: false,
    timer: 1500,
  });
}

function closeModal() {
  document.getElementById("close").click();
}

function limpiar() {
  (nombre.value = ""),
    (tipo.value = ""),
    (descripcion.value = ""),
    (archivo.value = "");
}

function alerta() {
  setTimeout(() => {
    alert.value = false;
    errores.value = "";
  }, 3000);
}

onMounted(async () => {
  setDocumentTitle(), await buscarMaterialesFormacion(), await traigodatos();
});

import { useConfiguracionStore } from "../stores/configuracion.js";
const useConfiguracion = useConfiguracionStore();
let coloroficialmenu = ref();
let coloroficialletra = ref();
let coloroficialtres = ref();
let coloroficialcuatro = ref();
async function traigodatos() {
  await useConfiguracion
    .traerConfiguracion()
    .then((resultado) => {
      coloroficialmenu.value = resultado[0].colormenu;
      coloroficialletra.value = resultado[0].colorletra;
      coloroficialtres.value = resultado[0].colortres;
      coloroficialcuatro.value = resultado[0].colorcuatro;
    })
    .catch((error) => {
      console.error("Hubo un error:", error);
    });
}
</script>

