<script setup>
import { ref, computed, onMounted } from "vue";
import { useProgramaStore } from "../stores/programa.js";
import { useUsuarioStore } from "../stores/usuario.js";
import { useMaterialFormacionStore } from "../stores/materialFormacion.js";

const useUsuario = useUsuarioStore();
const usePrograma = useProgramaStore();
const useMaterialFormacion = useMaterialFormacionStore();

let materialesFormacion = ref([]);
let vacio = ref("");
let buscado_v = ref("");
let materialId = ref("");
let materialEncontrado = ref([]);
let alert = ref(false);
let errores = ref("");
let newMatriales = ref([]);

const setDocumentTitle = () => {
  document.title = `COLMABE || Materiales ${usePrograma.idPrograma.nombre}`;
};

async function materialesPrograma() {
  await usePrograma.buscarProgramaId(usePrograma.idPrograma._id);
  materialesFormacion.value = usePrograma.idPrograma.materialformacion;
  newMatriales.value = [...materialesFormacion.value];
}

function material_filtrado(event) {
  buscado_v.value = event.target.value;
  if (buscado_v.value.trim() === "") {
    materialesPrograma();
    vacio.value = "";
  } else {
    materialEncontrado.value = newMatriales.value;
    let filtrado = materialesFormacion.value.filter((g) =>
      g.nombre.includes(buscado_v.value.trim().toLowerCase())
    );
    if (filtrado.length === 0) {
      vacio.value = `Sin resultados para ${buscado_v.value}`;
      materialesFormacion.value = [];
    } else {
      vacio.value = "";
      materialesFormacion.value = filtrado;
    }
  }
}

async function agregarMaterialFormacionPrograma() {
  if(materialId.value.trim()===''){
    alert.value = true;
        errores.value = 'Debe seleccionar un Material de formaci贸n';
        alerta();
  }else{
    await usePrograma
    .agregarMaterialFormacion(usePrograma.idPrograma._id, materialId.value)
    .then(() => {
      materialesPrograma();
      ok();
      document.getElementById("close1").click();
    })
    .catch((err) => {
      if (err.response && err.response.data.errors) {
        alert.value = true;
        errores.value = err.response.data.errors[0].msg;
        alerta();
      } else if (err.response && err.response.data.msg) {
        alert.value = true;
        errores.value = err.response.data.msg;
        alerta();
      }
    });
  }
}

async function buscarMateriales() {
  await useMaterialFormacion
    .buscarMaterialFormacion()
    .then((res) => {
      materialEncontrado.value = res;
    })
    .catch((err) => {
      console.log(err);
    });
}

function ok() {
  Swal.fire({
    position: "center",
    icon: "success",
    title: "Cambios guardados",
    showConfirmButton: false,
    timer: 1500,
  });
}

function alerta() {
  setTimeout(() => {
    alert.value = false;
    errores.value = "";
  }, 3000);
}

onMounted(async () => {
  await materialesPrograma(), traigodatos(), setDocumentTitle();
});

import { useConfiguracionStore } from "../stores/configuracion.js";
const useConfiguracion = useConfiguracionStore();
let coloroficialmenu = ref();
let coloroficialletra = ref();
let coloroficialtres = ref();
let coloroficialcuatro = ref();
async function traigodatos() {
  await useConfiguracion
    .traerConfiguracion()
    .then((resultado) => {
      coloroficialmenu.value = resultado[0].colormenu;
      coloroficialletra.value = resultado[0].colorletra;
      coloroficialtres.value = resultado[0].colortres;
      coloroficialcuatro.value = resultado[0].colorcuatro;
    })
    .catch((error) => {
      console.error("Hubo un error:", error);
    });
}
</script>

