<script setup>
import { ref, computed, onMounted } from "vue";
import { useUsuarioStore } from "../stores/usuario.js";
import { useRedStore } from "../stores/red.js";
import { useRolStore } from "../stores/rol.js";

const useUsuario = useUsuarioStore();
const useRed = useRedStore();
const useRol = useRolStore();

let usuarioEncontrado = ref([]);
let rolEncontrado = ref([]);
let msg_modal = ref("");
let buscado_v = ref("");
let vacio = ref("");
let id = ref("");
let codigo = ref("");
let nombre = ref("");
let apellidos = ref("");
let identificacion = ref("");
let fecha_nacimiento = ref("");
let genero = ref("");
let direccion = ref("");
let email = ref("");
let password = ref("");
let telefono = ref("");
let ocupacion = ref("");
let estado_civil = ref("");
let nacionalidad = ref("");
let contacto_emergencia = ref("");
let hoja_vida = ref("");
let archivo = ref("");
let redes = ref([]);
let redId = ref("");
var roles = ref([]);
let rolId = ref("");
let errores = ref("");
let alert = ref(false);
let photo = ref(null);
let len = ref(null);
let roles_activos = ref([]);
let img = ref("a");
let rolmodal = ref("");
let pageTitle = ref("");
let newUsuarios = ref([]);

const setDocumentTitle = () => {
  pageTitle.value = "Usuarios";
  document.title = `Legado Sena || ${pageTitle.value}`;
};

function cambio(msg) {
  if (msg == "Agregar") {
    buscarRed();
    buscarRol();
    msg_modal.value = "Agregar Usuario";
    limpiar();
  } else {
    msg_modal.value = "Editar Usuario";
  }
}

function llevar(event) {
  hoja_vida.value = event.target.files[0];
}

async function buscarUsuarios() {
  try {
    usuarioEncontrado.value = await useUsuario.buscarUsuario();
    newUsuarios.value = [...usuarioEncontrado.value];
    for (let i = 0; i < usuarioEncontrado.value.length; i++) {
      len.value = usuarioEncontrado.value[i]._id;
      await useUsuario
        .mostrarArchivoCloud(len.value)
        .then((res) => {
          photo.value = res.data.url;
        })
        .catch((err) => {
          console.log(err);
        });
    }
  } catch (error) {
    console.log(error);
  }
}

async function buscarRolId() {
  await useRol.buscarRolId(rolId.value);
  rolEncontrado.value = useRol.idRol;
}

async function buscarRed() {
  await useRed.buscarRed().then((res) => {
    redes.value = res;
  });
}

async function buscarRol() {
  await useRol.buscarRol().then((res) => {
    roles.value = res;
    roles_activos.value = roles.value.filter((rol) => rol.estado == true);
  });
}

function usuario_filtrado(event) {
  buscado_v.value = event.target.value;
  if (buscado_v.value === "") {
    buscarUsuarios();
    vacio.value = "";
  } else {
    usuarioEncontrado.value = newUsuarios.value;
    let filtrado = usuarioEncontrado.value.filter(
      (g) =>
        g.nombre.includes(buscado_v.value.toLowerCase()) ||
        g.identificacion.includes(buscado_v.value.toLowerCase())
    );
    if (filtrado.length === 0) {
      usuarioEncontrado.value = [];
      vacio.value = `Sin resultados para ${buscado_v.value}`;
    } else {
      vacio.value = "";
      usuarioEncontrado.value = filtrado;
    }
  }
}

function validaciones() {
  if (nombre.value.trim() === "") {
    alert.value = true;
    errores.value = "Complete el campo Nombre";
    alerta();
  } else if (apellidos.value.trim() === "") {
    alert.value = true;
    errores.value = "Complete el campo Apellidos";
    alerta();
  } else if (identificacion.value.trim() === "") {
    alert.value = true;
    errores.value = "Complete el campo Identificación";
    alerta();
  } else if (fecha_nacimiento.value.trim() === "") {
    alert.value = true;
    errores.value = "Complete el campo Fecha de Nacimiento";
    alerta();
  } else if (genero.value.trim() === "") {
    alert.value = true;
    errores.value = "Complete el campo Genero";
    alerta();
  } else if (direccion.value.trim() === "") {
    alert.value = true;
    errores.value = "Complete el campo Dirección";
    alerta();
  } else if (telefono.value.trim() === "") {
    alert.value = true;
    errores.value = "Complete el campo Telefono";
    alerta();
  } else if (email.value.trim() === "") {
    alert.value = true;
    errores.value = "Complete el campo Email";
    alerta();
  } else if (password.value.trim() === "") {
    alert.value = true;
    errores.value = "Complete el campo Contraseña";
    alerta();
  } else if (ocupacion.value.trim() === "") {
    alert.value = true;
    errores.value = "Complete el campo Ocupación";
    alerta();
  } else if (estado_civil.value.trim() === "") {
    alert.value = true;
    errores.value = "Complete el campo Estado sivil";
    alerta();
  } else if (nacionalidad.value.trim() === "") {
    alert.value = true;
    errores.value = "Complete el campo Nacionalidad";
    alerta();
  } else if (contacto_emergencia.value.trim() === "") {
    alert.value = true;
    errores.value = "Complete el campo Contacto de Emergencia";
    alerta();
  } else if (hoja_vida.value === "") {
    alert.value = true;
    errores.value = "Falta lla Hoja de Vida";
    alerta();
  } else if (rolId.value.trim() === "") {
    alert.value = true;
    errores.value = "Debe seleccionar un Rol de Usuario";
    alerta();
  } else {
    return true;
  }
}

async function registrarUsuario() {
  if (validaciones() === true) {
    await useUsuario
      .registrarUsuario({
        nombre: nombre.value.toLowerCase(),
        apellidos: apellidos.value.toLowerCase(),
        identificacion: identificacion.value.toLowerCase(),
        fecha_nacimiento: fecha_nacimiento.value.toLowerCase(),
        genero: genero.value.toLowerCase(),
        direccion: direccion.value.toLowerCase(),
        telefono: telefono.value.toLowerCase(),
        email: email.value.toLowerCase(),
        password: password.value.toLowerCase(),
        ocupacion: ocupacion.value.toLowerCase(),
        estado_civil: estado_civil.value.toLowerCase(),
        nacionalidad: nacionalidad.value.toLowerCase(),
        contacto_emergencia: contacto_emergencia.value.toLowerCase(),
        hoja_vida: hoja_vida.value,
        estado: true,
        rol: rolId.value,
        red: redId.value,
      })
      .then(() => {
        buscarUsuarios();
        console.log("Registro exitoso");
        ok();
        closeModal();
        limpiar();
      })
      .catch((err) => {
        if (err.response && err.response.data.errors) {
          alert.value = true;
          errores.value = err.response.data.errors[0].msg;
          alerta();
        } else if (err.response && err.response.data.msg) {
          alert.value = true;
          errores.value = err.response.data.msg;
          alerta();
        } else if (err.response.data.error) {
          alert.value = true;
          errores.value = "Falta el archivo";
          alerta();
        }
      });
  }
}

