function editar(u) {
  buscarRed();
  buscarRol();
  id.value = u._id;
  nombre.value = u.nombre;
  apellidos.value = u.apellidos;
  identificacion.value = u.identificacion;
  fecha_nacimiento.value = u.fecha_nacimiento;
  genero.value = u.genero;
  direccion.value = u.direccion;
  telefono.value = u.telefono;
  (email.value = u.email),
    (password.value = u.password),
    (ocupacion.value = u.ocupacion);
  estado_civil.value = u.estado_civil;
  nacionalidad.value = u.nacionalidad;
  contacto_emergencia.value = u.contacto_emergencia;
  hoja_vida.value = u.hoja_vida;
  archivo.value = u.archivo;
  rolId.value = u.rol._id;
  if (u.red) {
    redId.value = u.red._id;
  }
}

async function guardarEdision() {
  if (validaciones() === true) {
    await useUsuario
      .editarUsuario(
        id.value,
        nombre.value.toLowerCase(),
        apellidos.value.toLowerCase(),
        identificacion.value.toLowerCase(),
        fecha_nacimiento.value.toLowerCase(),
        genero.value.toLowerCase(),
        direccion.value.toLowerCase(),
        telefono.value.toLowerCase(),
        email.value,
        password.value,
        ocupacion.value.toLowerCase(),
        estado_civil.value.toLowerCase(),
        nacionalidad.value.toLowerCase(),
        contacto_emergencia.value.toLowerCase(),
        hoja_vida.value,
        archivo.value,
        rolId.value,
        redId.value
      )
      .then(() => {
        buscarUsuarios();
        console.log("Edisión exitosa");
        ok();
        closeModal();
        limpiar();
      })
      .catch((err) => {
        console.log(err);
        if (err.response && err.response.data.errors) {
          alert.value = true;
          errores.value = err.response.data.errors[0].msg;
          alerta();
        } else if (err.response && err.response.data.msg) {
          alert.value = true;
          errores.value = err.response.data.msg;
          alerta();
        } else if (err.response.data.error) {
          alert.value = true;
          errores.value = "Falta el archivo";
          alerta();
        }
      });
  }
}

async function cambiarEstado(u) {
  if (u.estado === true) {
    u.estado = false;
  } else {
    u.estado = true;
  }
  await useUsuario
    .cambiarEstado(u._id, u.estado)
    .then(() => {
      console.log("Cambio exitoso");
    })
    .catch((err) => {
      console.log(err);
    });
}

